// Suppose this is the string you got from the AI Agent node:
const aiOutput =$input.first().json.output ;

// Function to extract with regex
function extractParts(str) {
  const result = {};

  // Extract subject: between "**Subject:**" and newline
  const subjMatch = str.match(/\*\*Subject:\*\*\s*([^\n]+)/);
  if (subjMatch) {
    result.subject = subjMatch[1].trim();
  }

  // Extract text: between "**Text:**" and the "***" divider
  const textMatch = str.match(/\*\*Text:\*\*\s*([\s\S]*?)\n\*\*\*/);
  if (textMatch) {
    result.text = textMatch[1].trim();
  }

  // Extract tool_code block content: inside <tool_code> ... </tool_code>
  const toolMatch = str.match(/<tool_code>\s*([\s\S]*?)\s*<\/tool_code>/);
  if (toolMatch) {
    result.toolCode = toolMatch[1].trim();
  }

  // If you also want to extract the send_mail parameters, parse inside toolCode:
  if (result.toolCode) {
    const sendMailMatch = result.toolCode.match(/send_mail\s*\(\s*to\s*=\s*"([^"]+)"\s*,\s*subject\s*=\s*"([^"]+)"\s*,\s*body\s*=\s*"([\s\S]*)"\s*\)/);
    if (sendMailMatch) {
      result.to = sendMailMatch[1];
      result.subject2 = sendMailMatch[2];
      // Note: body may contain escaped newlines (“\\n”), so you may want to unescape
      result.body = sendMailMatch[3].replace(/\\n/g, "\n");
    }
  }

  return result;
}

// Test extraction:
const parts = extractParts(aiOutput);
console.log(parts);
