// Enhanced chunking for better retrieval
const items = $input.all();

const processedChunks = items.flatMap((item, pageIndex) => {
    const data = item.json;
    const chunks = [];

    // Create metadata base
    const baseMeta = {
        url: data.url || "",
        title: data.title || "Untitled",
        type: data.course?.courseTitle ? "course" : "page",
        courseTitle: data.course?.courseTitle || "",
        canonical: data.canonical || "",
    };

    // 1. Course Description (if exists) - High priority
    if (data.course?.courseDesc) {
        const desc = data.course.courseDesc;
        const descChunks = chunkText(desc, 1200, 150);
        descChunks.forEach((chunk, idx) => {
            chunks.push({
                json: {
                    text: `Course: ${baseMeta.courseTitle}\n\n${chunk}`,
                    metadata: {
                        ...baseMeta,
                        contentType: "course_description",
                        chunkIndex: idx,
                        priority: "high",
                    },
                },
            });
        });
    }

    if (data.outlineItems && data.outlineItems.length > 0) {
        const outlineText =
            `Course OUtline for ${baseMeta.courseTitle}:\n` +
            data.outlineItems.map((item, i) => `${i + 1}. ${item}`).join("\n");
        const outlineChunks = chunkText(outlineText, 1000, 100);
        outlineChunks.forEach((chunk, idx) => {
            chunks.push({
                json: {
                    text: chunk,
                    metadata: {
                        ...baseMeta,
                        contentType: "course_outline",
                        chunkIndex: idx,
                        priority: "medium",
                    },
                },
            });
        });
    }

    if (data.rawText || data.metaDescription) {
        const mainContent = [
            data.metaDescription ? `Description: ${data.metaDescription}` : "",
            data.rawText || "",
        ]
            .filter(Boolean)
            .join("\n\n");

        const contentChunks = chunkText(mainContent, 1200, 150);
        contentChunks.forEach((chunk, idx) => {
            chunks.push({
                json: {
                    text: `${baseMeta.title}\n\n${chunk}`,
                    metadata: {
                        ...baseMeta,
                        contentType: "main_content",
                        chunkIndex: idx,
                        priority: "medium",
                    },
                },
            });
        });
    }

    if (data.headings && data.headings.length > 0) {
        const headingsText = data.headings
            .map((h) => `${h.tag}: ${h.text}`)
            .join("\n");

        if (headingsText.length > 100) {
            chunks.push({
                json: {
                    text: `Page Structure: \n${headingsText}`,
                    metadata: {
                        ...baseMeta,
                        contentType: "headings",
                        priority: "low",
                    },
                },
            });
        }
    }

    if (data.lists && data.lists.length > 0) {
        data.lists.forEach((list, listIdx) => {
            const listText =
                `${list.nearestHeading || "List"}:\n` +
                list.items.map((item, i) => `• ${item}`).join("\n");
            chunks.push({
                json: {
                    metadata: {
                        ...baseMeta,
                        contentType: "list",
                        listIndex: listIdx,
                        priority: "low",
                    },
                },
            });
        });
    }

    return chunks;
});

function chunkText(text, chunkSize = 1200, overlap = 150) {
    if (!text || text.length <= chunkSize) return [text];

    const chunks = [];
    let start = 0;

    while (start < text.length) {
        let end = start + chunkSize;
        const lastPeriod = text.lastIndexOf(". ", end);
        const lastNewLine = text.lastIndexOf("\n", end);
        const breakPoint = Math.max(lastPeriod, lastNewLine);

        if (breakPoint > start + chunkSize * 0.5) {
            end = breakPoint + 1;
        }

        chunks.push(text.slice(start, end).trim());

        start = end - overlap;
    }

    return chunks.filter((chunk) => chunks.length > 50);
}
return processedChunks;

// n8n Function node code (no external modules)
// Input: items = [{ json: <scrape-object> }, ...]
// Output: one cleaned item per input (no chunking)

function normalizeText(t){
  if(!t) return "";
  t = t.replace(/\\n/g, "\n").replace(/\\r/g, "\n");
  t = t.replace(/\u0002/g," ").replace(/\\u0002/g," ");
  t = t.replace(/Digest:\s*\d+/gi, "");
  t = t.replace(/Application error:.*$/gmi, "");
  t = t.replace(/\s+/g, " ").trim();
  return t;
}

function isBoilerplateHeading(h, globalNavSet){
  if(!h) return false;
  const hp = h.trim().toLowerCase();
  if(hp.length < 3) return true;
  const boiler = ["apply online","personal information","more info","contact us","course catalog","master your skills"];
  if(boiler.includes(hp)) return true;
  if(globalNavSet && globalNavSet.has(hp)) return true;
  return false;
}

// Simple deterministic JS hash (djb2 variant) — works in n8n Function node
function simpleHash(str){
  let hash = 5381;
  for (let i = 0; i < str.length; i++) {
    hash = ((hash << 5) + hash) + str.charCodeAt(i); // hash * 33 + c
    // keep in 32-bit integer range
    hash = hash & 0xFFFFFFFF;
  }
  // Convert to unsigned 32-bit hex string
  return (hash >>> 0).toString(16);
}

// Ensure items exists (n8n provides it)
const pages = (items || []).map(i => i.json || i);
const headingFreq = {};
pages.forEach(p => {
  (p.headings||[]).forEach(h => {
    const key = (h.text||"").trim().toLowerCase();
    if(!key) return;
    headingFreq[key] = (headingFreq[key]||0) + 1;
  });
});
const globalNavSet = new Set(Object.keys(headingFreq).filter(k => headingFreq[k] > 5)); // adjust threshold

const seenFingerprints = new Set();
const out = [];

for(const p of pages){
  const url = p.url || p.canonical || "";
  const title = (p.title || "").trim();
  const raw = (p.rawText || "").toString();

  // quick quality guards
  if(/application error/i.test(raw) || /digest:\s*\d+/i.test(raw)){
    out.push({ json: { url, title, cleaned_text:"", metadata: { quality: "error_page", raw_excerpt: raw.slice(0,200) } }});
    continue;
  }

  // Compose cleaned text from good fields (prioritize courseDesc, rawText, headings+lists)
  let bodyParts = [];
  if(p.course && p.course.courseDesc) bodyParts.push(p.course.courseDesc);
  if(raw) bodyParts.push(raw);

  const safeHeadings = (p.headings||[]).map(h=>h.text||"").filter(h=>!isBoilerplateHeading(h, globalNavSet));
  if(safeHeadings.length) bodyParts.unshift(safeHeadings.join(" — "));

  (p.lists||[]).forEach(L=>{
    if(L.items && L.items.length){
      bodyParts.push(L.items.join(", "));
    }
  });

  let cleaned_text = normalizeText(bodyParts.join("\n\n"));
  // Collapse simple repeated words/phrases
  cleaned_text = cleaned_text.replace(/\b(.+?)\b(?:\s+\1\b){2,}/gi, "$1");

  // fingerprint for dedupe using simpleHash
  const fingerprint = simpleHash(cleaned_text.slice(0,2000) || url || title);

  if(seenFingerprints.has(fingerprint)) {
    out.push({ json: { url, title, cleaned_text:"", metadata: { duplicate_of: fingerprint } }});
    continue;
  }
  seenFingerprints.add(fingerprint);

  // Build metadata
  const metadata = {
    url,
    canonical: p.canonical || null,
    source: "edifycit.com",
    title,
    headings: safeHeadings,
    internalLinks: p.internalLinks || [],
    paragraphCount: p.paragraphCount || 0
  };

  // Extract contact/email/phone if present (simple regex)
  const emailMatch = (p.rawText||"").match(/[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}/i);
  if(emailMatch) metadata.contact_email = emailMatch[0];
  const phoneMatch = (p.rawText||"").match(/\+?\d[\d\-\s]{6,}\d/);
  if(phoneMatch) metadata.contact_phone = phoneMatch[0].replace(/\s+/g,' ');

  out.push({ json: { url, title, cleaned_text, metadata } });
}

return out;
