// Clean and extract JSON workflow from AI agent output
function extractWorkflowJSON(aiOutput) {
  try {
    if (!aiOutput || typeof aiOutput !== 'string') {
      throw new Error('Invalid input: expected string output from AI agent');
    }

    // Remove various markdown code block markers
    let cleanedOutput = aiOutput
      .replace(/```json\s*/gi, '')
      .replace(/```javascript\s*/gi, '')
      .replace(/```\s*/g, '')
      .replace(/^json\s*/gi, ''); // Remove standalone 'json' at start

    // Remove any explanatory text before the JSON
    const lines = cleanedOutput.split('\n');
    let startIndex = -1;

    // Find the line that starts the JSON object
    for (let i = 0; i < lines.length; i++) {
      const trimmedLine = lines[i].trim();
      if (trimmedLine.startsWith('{')) {
        startIndex = i;
        break;
      }
    }

    if (startIndex !== -1) {
      cleanedOutput = lines.slice(startIndex).join('\n');
    }

    // Remove any trailing explanatory text after the JSON
    cleanedOutput = cleanedOutput.trim();

    // Find the complete JSON object boundaries
    const firstBrace = cleanedOutput.indexOf('{');
    let braceCount = 0;
    let lastBrace = -1;

    if (firstBrace === -1) {
      throw new Error('No opening brace found in the output');
    }

    // Find the matching closing brace
    for (let i = firstBrace; i < cleanedOutput.length; i++) {
      if (cleanedOutput[i] === '{') braceCount++;
      if (cleanedOutput[i] === '}') {
        braceCount--;
        if (braceCount === 0) {
          lastBrace = i;
          break;
        }
      }
    }

    if (lastBrace === -1) {
      throw new Error('No matching closing brace found');
    }

    // Extract the JSON string
    const jsonString = cleanedOutput.substring(firstBrace, lastBrace + 1);

    // Validate JSON and clean up any formatting issues
    const parsedJSON = JSON.parse(jsonString);

    // Ensure it has the basic workflow structure
    if (!parsedJSON.nodes || !Array.isArray(parsedJSON.nodes)) {
      throw new Error('Invalid workflow JSON: missing or invalid nodes array');
    }

    if (!parsedJSON.connections || typeof parsedJSON.connections !== 'object') {
      throw new Error('Invalid workflow JSON: missing or invalid connections object');
    }

    // Return the parsed JSON object (not stringified) for n8n
    return parsedJSON;

  } catch (error) {
    console.error('Error extracting JSON:', error);
    console.error('Input was:', aiOutput);
    throw new Error(`Failed to extract valid JSON: ${error.message}`);
  }
}

